namespace WW.Test {
    public class Polygon2DTest {
        public List<Polygon2D> ExclusiveOrExample() {
            Polygon2D polygon1 =
                new Polygon2D(
                    new Point2D(2d, 1d),
                    new Point2D(5d, 2d),
                    new Point2D(4d, 5d),
                    new Point2D(1d, 4d)
                );
            Polygon2D polygon2 =
                new Polygon2D(
                    new Point2D(3d, -1d),
                    new Point2D(7d, 1d),
                    new Point2D(6d, 4d),
                    new Point2D(-1d, 2d)
                );
            List<Polygon2D> result = Polygon2D.GetExclusiveOr(
                new Polygon2D[] { polygon1 },
                new Polygon2D[] { polygon2 }
            );
            return result;
        }
    }
}

